        int result = 0;
        const auto& R = RLE(S);
        for (const auto& word : words) {
            const auto& R2 = RLE(word);
            if (R.key != R2.key) {
                continue;
            }
            int i;
            for (i = 0; i < R.counts.size(); ++i) {
               const auto& c1 = R.counts[i];
               const auto& c2 = R2.counts[i];
               if (c1 < max(c2, 3) && c1 != c2) {
                   break;
               }
            }
            if (i == R.counts.size()) {
                ++result;
            }
        }
        return result;
    }

private:
    struct RLE {
        string key;
        vector<int> counts;
        RLE(const string& s) {
            int prev = -1;
            for (int i = 0; i < s.length(); ++i) {
                if (i == s.length() - 1 || s[i] != s[i + 1]) {
                    key.push_back(s[i]);
                    counts.emplace_back(i - prev);
                    prev = i;
                }
            }
        }
    };
};

// Time:  O(n^4)
// Space: O(n^2)

class Solution {
public:
    vector<string> ambiguousCoordinates(string S) {
        vector<string> result;
        for (int i = 1; i < S.length() - 2; ++i) {
            const auto& lefts = make(S, 1, i);
            const auto& rights = make(S, i + 1, S.length() - 2 - i);
            for (const auto& left : lefts) {
                for (const auto& right : rights) {
                    string s;
                    s += "(";
                    s += left;
                    s += ", ";
                    s += right;
                    s += ")";
                    result.emplace_back(move(s));
                }
            }
        }
        return result;
    }

private:
    // Time:  O(n^2)
    // Space: O(n^2)
    vector<string> make(const string& S, int i, int n) {
        vector<string> result;
        for (int d = 1; d <= n; ++d) {
            const auto& left = S.substr(i, d);
            const auto& right = S.substr(i + d, n - d);
            if ((left.front() != '0' || left == "0") &&
                 right.back() != '0') {
                string s;
                s += left;
                s += (!right.empty() ? "." : "");
                s += right;
                result.emplace_back(move(s));
            }
        }
        return result;
    }
};

