class Solution {
public:
    vector<int> diStringMatch(string S) {
        vector<int> result;
        int left = 0, right = S.length();
        for (const auto& c : S) {
            if (c == 'I') {
                result.emplace_back(left);
                ++left;
            } else {
                result.emplace_back(right);
                --right;
            }
        }
        result.emplace_back(left);
        return result;
    }
};

