// Time:  O(nlogn * l), n is the length of files, l is the average length of strings
// Space: O(l)

class Solution {
public:
    vector<string> reorderLogFiles(vector<string>& logs) {
        auto pivot = stable_partition(logs.begin(), logs.end(),
                                      []( const auto& log ) {
                                          return isalpha(log.back());
                                      });
        sort(logs.begin(), pivot,
             [](const auto& a, const auto& b) {
                 int i = a.find_first_of(' '), j = b.find_first_of(' ');
                 return a.substr(i) != b.substr(j) ?
                            a.substr(i) < b.substr(j) :
                            a.substr(0, i) < b.substr(0, j);
             });
        return logs;
    }
};

// Time:  O(nlogn)
// Space: O(n)

// bit solution
class Solution {
public:
    vector<int> pancakeSort(vector<int>& arr) {
        BIT bit(size(arr));
        vector<int> result;
        for (int i = 0; i < size(arr); ++i) {
            int n = bit.query((arr[i] - 1) - 1);
            bit.add(arr[i] - 1, 1);
            if (n == i) {  // already sorted
                continue;
            }
            if (n == 0) {                    // (0..i-1)i
                if (i > 1) {
                    result.emplace_back(i);  // (i-1..0)i
                }
                result.emplace_back(i + 1);  // i(0..i-1)
            } else {                         // (0..n-1)n(n+1..i-1)i
                if (n > 1) {
                    result.emplace_back(n);  // (n-1..0)n(n+1..i-1)i
                }
                result.emplace_back(i);      // (i-1..n+1)n(0..n-1)i
                result.emplace_back(i + 1);  // i(n-1..0)n(n+1..i-1)
                result.emplace_back(n + 1);  // (0..n-1)in(n+1..i-1)
            }
        }
        return result;
    }

private:
    class BIT {
    public:
        BIT(int n) : bit_(n + 1) {  // 0-indexed
        }

        void add(int i, int val) {
            ++i;
            for (; i < size(bit_); i += lower_bit(i)) {
                bit_[i] += val;
            }
        }

        int query(int i) const {
            ++i;
            int total = 0;
            for (; i > 0; i -= lower_bit(i)) {
                total += bit_[i];
            }
            return total;
        }

    private:
        int lower_bit(int i) const {
            return i & -i;
        }

        vector<int> bit_;
    };
};

