        int start = val, end = val;
        if (it != intervals_.begin() && prev(it)->end + 1 >= val) {
            --it;
        }
        while (it != intervals_.end() && end + 1 >= it->start) {
            start = min(start, it->start);
            end = max(end, it->end);
            it = intervals_.erase(it);
