            int triage_idx = 0;
            for (int i = 0; i < frontiers.size(); ++i) {
                if (frontiers[i].size() > frontiers[triage_idx].size()) {
                    triage_idx = i;
                }
            }
            for (int i = 0; i < regions.size(); ++i) {
                if (i == triage_idx) {
                    result += perimeters[i];
                    for (const auto& p : regions[i]) {
                        grid[p.first][p.second] = -1;
                    }
                    continue;
                }
                for (const auto& p : regions[i]) {
                    for (const auto& d : directions) {
                        int nr = p.first + d.first;
                        int nc = p.second + d.second;
                        if (nr < 0 || nr >= grid.size() ||
                            nc < 0 || nc >= grid[nr].size()) {
                            continue;
                        }
                        if (grid[nr][nc] == 0) {
                            grid[nr][nc] = 1;
                        }
                    }
                }
            }
        }

        return result;
    }

private:
    template <typename T>
    struct PairHash {
        size_t operator()(const pair<T, T>& p) const {
            size_t seed = 0;
            seed ^= std::hash<T>{}(p.first)  + 0x9e3779b9 + (seed<<6) + (seed>>2);
            seed ^= std::hash<T>{}(p.second) + 0x9e3779b9 + (seed<<6) + (seed>>2);
            return seed;
        }
    };
    using P = pair<int, int>;
    using P_SET = unordered_set<P, PairHash<int>>;

    void dfs(const vector<vector<int>>& grid, 
             const P& p,
             P_SET *lookup,
             vector<P_SET> *regions,
             vector<P_SET> *frontiers, 
             vector<int> *perimeters) {

        if (lookup->count(p)) {
            return;
        }
        lookup->emplace(p);
        regions->back().emplace(p);
        for (const auto& d : directions) {
            int nr = p.first + d.first;
            int nc = p.second + d.second;
            if (nr < 0 || nr >= grid.size() ||
                nc < 0 || nc >= grid[nr].size()) {
                continue;
            }
            if (grid[nr][nc] == 1) {
                dfs(grid, make_pair(nr, nc), lookup, regions, frontiers, perimeters);
            } else if (grid[nr][nc] == 0) {
                frontiers->back().emplace(nr, nc);
                ++perimeters->back();
            }
        }
    }
    const vector<P> directions = {{0, -1}, {0, 1}, {-1, 0}, {1, 0}};
};

// Time:  O(k^n)
// Space: O(k^n)

// https://en.wikipedia.org/wiki/De_Bruijn_sequence
// https://en.wikipedia.org/wiki/Lyndon_word
class Solution {
public:
    string crackSafe(int n, int k) {
        const int M = pow(k, n - 1);
        vector<int> P;
        for (int i = 0; i < k; ++i) {
            for (int q = 0; q < M; ++q) {
                P.emplace_back(q * k + i);  // rotate: i*k^(n-1) + q => q*k + i
            }
        }
        const int total = pow(k, n);
        string result(n - 1, '0' + k - 1);
        for (int i = 0; i < total; ++i) {
            int j = i;
            // concatenation in lexicographic order of Lyndon words
            while (P[j] >= 0) {
                result.push_back('0' + j / M);
                auto Pj = P[j];
                P[j] = -1;
                j = Pj;
            }
        }
        return result;
    }
};

// Time:  O(k^n)
// Space: O(k^n)
class Solution2 {
public:
    string crackSafe(int n, int k) {
        const int M = pow(k, n - 1);
        int unique_rolling_hash = 0;
        string result(n - 1, '0');
        unordered_set<int> lookup;
        const int total = pow(k, n);
        while (lookup.size() < total) {
            for (int i = k - 1; i >= 0; --i) {  // preorder like traversal relative to initial result to avoid getting stuck, i.e. don't use 0 until there is no other choice
                const auto& new_unique_rolling_hash = unique_rolling_hash * k + i;
                if (!lookup.count(new_unique_rolling_hash)) {
                    lookup.emplace(new_unique_rolling_hash);
                    result.push_back('0' + i);
                    unique_rolling_hash = new_unique_rolling_hash % M;
                    break;
                }
            }
        }
        return result;
    }
};

