        sort(P.begin(), P.end());

        // Build lower hull
        for (size_t i = 0; i < n; ++i) {
            while (k >= 2 && cross(H[k-2], H[k-1], P[i]) < 0) k--; // modified
            H[k++] = P[i];
        }

        // Build upper hull
        for (size_t i = n-1, t = k+1; i > 0; --i) {
            while (k >= t && cross(H[k-2], H[k-1], P[i-1]) < 0) k--; // modified
            H[k++] = P[i-1];
        }

        H.resize(--k);        
        if (H[1] == H[k - 1]) {  // modified
            H.resize(k / 2 + 1);
        }
        return H;
    }
};

// Time:  O(n^2)
// Space: O(1)

class Solution {
public:
    int surfaceArea(vector<vector<int>>& grid) {
        int result = 0;
        for (int i = 0; i < grid.size(); ++i) {
            for (int j = 0; j < grid.size(); ++j) {
                if (grid[i][j]) {
                    result += grid[i][j] * 4 + 2;
                }
                if (i) {
                    result -= min(grid[i][j], grid[i - 1][j]) * 2;
                }
                if (j) {
                    result -= min(grid[i][j], grid[i][j - 1]) * 2;
                }
            }
        }
        return result;
    }
};

